mapper.select:
        SELECT


--         级联表的字段
        ,fuzhaung.fuzhaung_uuid_number as fuzhaungUuidNumber
        ,fuzhaung.fuzhaung_name as fuzhaungName
        ,fuzhaung.fuzhaung_photo as fuzhaungPhoto
        ,fuzhaung.fuzhaung_types as fuzhaungTypes
        ,fuzhaung.fuzhaung_kucun_number as fuzhaungKucunNumber
        ,fuzhaung.fuzhaung_price as fuzhaungPrice
        ,fuzhaung.fuzhaung_old_money as fuzhaungOldMoney
        ,fuzhaung.fuzhaung_new_money as fuzhaungNewMoney
        ,fuzhaung.fuzhaung_clicknum as fuzhaungClicknum
        ,fuzhaung.fuzhaung_content as fuzhaungContent
        ,fuzhaung.shangxia_types as shangxiaTypes
        ,fuzhaung.fuzhaung_delete as fuzhaungDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_sum_jifen as yonghuSumJifen
        ,yonghu.yonghu_new_jifen as yonghuNewJifen
        ,yonghu.huiyuandengji_types as huiyuandengjiTypes

        FROM cart  a
        left JOIN fuzhaung fuzhaung ON a.fuzhaung_id = fuzhaung.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and (
                    a.fuzhaung_id = #{params.fuzhaungId}
                )


                  and a.buy_number >= #{params.buyNumberStart}   


                  and a.buy_number <= #{params.buyNumberEnd}   


                and a.buy_number = #{params.buyNumber}


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.fuzhaung_id IS NOT NULL


                and fuzhaung.fuzhaung_uuid_number like CONCAT('%',#{params.fuzhaungUuidNumber},'%')


                and fuzhaung.fuzhaung_name like CONCAT('%',#{params.fuzhaungName},'%')


                and fuzhaung.fuzhaung_types = #{params.fuzhaungTypes}



                  and fuzhaung.fuzhaung_kucun_number >= #{params.fuzhaungKucunNumberStart}   


                  and fuzhaung.fuzhaung_kucun_number <= #{params.fuzhaungKucunNumberEnd}   


                and fuzhaung.fuzhaung_kucun_number = #{params.fuzhaungKucunNumber}


                  and fuzhaung.fuzhaung_price >= #{params.fuzhaungPriceStart}   


                  and fuzhaung.fuzhaung_price <= #{params.fuzhaungPriceEnd}   


                and fuzhaung.fuzhaung_price = #{params.fuzhaungPrice}


                  and fuzhaung.fuzhaung_old_money >= #{params.fuzhaungOldMoneyStart}   


                  and fuzhaung.fuzhaung_old_money <= #{params.fuzhaungOldMoneyEnd}   


                  and fuzhaung.fuzhaung_new_money >= #{params.fuzhaungNewMoneyStart}   


                  and fuzhaung.fuzhaung_new_money <= #{params.fuzhaungNewMoneyEnd}   


                  and fuzhaung.fuzhaung_clicknum >= #{params.fuzhaungClicknumStart}   


                  and fuzhaung.fuzhaung_clicknum <= #{params.fuzhaungClicknumEnd}   


                and fuzhaung.fuzhaung_clicknum = #{params.fuzhaungClicknum}


                and fuzhaung.fuzhaung_content like CONCAT('%',#{params.fuzhaungContent},'%')


                and fuzhaung.shangxia_types = #{params.shangxiaTypes}



                  and fuzhaung.fuzhaung_delete >= #{params.fuzhaungDeleteStart}   


                  and fuzhaung.fuzhaung_delete <= #{params.fuzhaungDeleteEnd}   


                and fuzhaung.fuzhaung_delete = #{params.fuzhaungDelete}



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


                  and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


                  and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


                  and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


                  and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   


                and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}




        order by a.${params.orderBy} desc
